mapper.select:
SELECT id, agent_id, client_id_from, client_id_to,`condition`,step_prompt,create_time
FROM ai_agent_client_line
WHERE id = #{id}

SELECT id, agent_id, client_id_from, client_id_to,step_prompt,`condition`,create_time
FROM ai_agent_client_line
WHERE agent_id = #{agentId}

SELECT id, agent_id, client_id_from, create_time
FROM ai_agent_client_line
WHERE client_id_from = #{clientIdFrom}

SELECT id, agent_id, client_id_from, create_time
FROM ai_agent_client_line
WHERE agent_id = #{agentId} AND client_id_from = #{clientIdFrom}

mapper.insert:
INSERT INTO ai_agent_client_line (
    agent_id, client_id_from, client_id_to,step_prompt,`condition`
) VALUES (
    #{agentId}, #{clientIdFrom}, #{clientIdTo},#{stepPrompt}, #{condition}
)

mapper.select:
SELECT ai_agent_client_line.id, ai_agent_client_line.agent_id, ai_agent_client_line.client_id_from, ai_agent_client_line.client_id_to, ai_agent_client_line.condition, ai_agent_client_line.step_prompt, ai_agent_client_line.create_time, FROM ai_agent_client_line LEFT JOIN ai_client ON ai_agent_client_line.client_id_from = ai_client.id
AND id = #{id}
AND agent_id = #{agentId}
AND client_id_from = #{clientIdFrom}
AND client_id_to = #{clientIdTo}
ORDER BY agent_id, sequence, create_time DESC
LIMIT #{offset}, #{limit}
