mapper.select:
SELECT id, advisor_name, advisor_type, embedding_model_id,database_id, ext_param, status, create_time, update_time
FROM ai_client_advisor
WHERE id = #{id}

SELECT id, advisor_name, advisor_type,embedding_model_id ,database_id, ext_param, status, create_time, update_time
FROM ai_client_advisor
WHERE advisor_name = #{advisorName}

mapper.insert:
INSERT INTO ai_client_advisor (
    advisor_name, advisor_type,embedding_model_id,database_id ,ext_param, status, create_time, update_time
) VALUES (
             #{advisorName}, #{advisorType},#{embeddingModelId},#{databaseId},#{extParam}, #{status}, #{createTime}, #{updateTime}
         )

mapper.select:
SELECT a.id, a.advisor_name, a.advisor_type, a.ext_param,a.database_id,a.embedding_model_id, a.status, a.create_time, a.update_time FROM ai_client_advisor a WHERE a.id IN ( SELECT DISTINCT c.advisor_id FROM ai_client_advisor_config c WHERE c.client_id IN
#{clientId}
) ORDER BY a.order_num, a.id

SELECT
id, advisor_name, advisor_type, ext_param, status, create_time, update_time,database_id,embedding_model_id
FROM
ai_client_advisor


        AND id = #{id}


        AND advisor_name LIKE CONCAT('%', #{advisorName}, '%')


        AND advisor_type = #{advisorType}


        AND status = #{status}


ORDER BY  create_time DESC

    LIMIT #{offset}, #{limit}
